        this.handwritingNode.show();

        var zidx = this.node.getStyle("z-index");
        this.handwritingNode.setStyles({
            "z-index": ( this.isNumber(zidx) ? parseInt(zidx) : 0) +1
        });
        if( this.status === "popup" ){
